
public class pattern1 {
	
	public static void pattern(int a) {
		for(int i=0;i<=a;i++) {
			System.out.print("\n ");
			for(int j=0;j<=i;j++ )
			{
				System.out.print("* ");
			}
	}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
			int n = 5;
			pattern(n);
		}
	}
